            bubbleSetting.StrokeThickness = 5;
            bubbleSetting.MaxSize = 500;
            bubbleSetting.MinSize = 100;
            layer.BubbleMarkerSetting = bubbleSetting;
            syncMap.Layers.Add(layer);


        }       

    }
