namespace MapApp
{
    public sealed partial class MainPage : Page
    {
        public MainPage()
        {
            this.InitializeComponent();           
            SfMap syncMap = new SfMap();
            syncMap.MinZoom = 3;
            ShapeFileLayer layer = new ShapeFileLayer();          
